
public class E4_14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n,i,j;
		for(i=1;i<=4;i++)
		{
			for(j=1;j<=i;j++)
			{
				System.out.printf("1 ");
		
			}
			for(j=1;j<=4-i;j++)
			{
				System.out.printf("0 ");
			}
			System.out.println();
		
		}
	}

}
